import React, { useState, useEffect } from "react";

const FilteredView = () => {
  const [items, setItems] = useState([]);

  filterByDate = (pickedDate) => {
    const byDate = this.state.items.filter((item, i) => {
      return item.date === pickedDate;
    });
    return <FilteredView items={byDate} />;
  };

  function getData() {
    fetch("http://localhost:9000/getitems")
      .then((res) => res.json())
      .then((res) => {
        const sortedActivities = res.sort(
          (a, b) => new Date(b.date) - new Date(a.date)
        );
        setItems(sortedActivities);
      });
  }

  getData();

  const tableRows = items.map((item, i) => {
    console.log("items: " + item);
    return (
      <tr>
        <td>{item.type}</td>
        <td>{item.place}</td>
        <td>{item.date}</td>
        <td>{item.amount}</td>
      </tr>
    );
  });

  return (
    <table className="table">
      <thead>
        <tr>
          <th scope="col">Type</th>
          <th scope="col">Place</th>
          <th scope="col">Date</th>
          <th scope="col">Amount</th>
        </tr>
      </thead>
      <tbody>{tableRows}</tbody>
    </table>
  );
};

export default FilteredView;
