import React from "react";
import ListItem from "./ListItem";
import MaterialTable from "./MaterialUITable";

function GetItems(items, itemtype) {
  return items.filter((value, i) => {
    return value.type === itemtype;
  });
}

function GetTotal(data) {
  const total = data.reduce(
    (totalAmount, item) => totalAmount + parseInt(item.amount, 10),
    0
  );

  return total;
}

const CardHeader = (props) => {
  const { type, data } = props;
  const id = "#" + type;
  const total = GetTotal(data);
  return (
    <div className="card-header" id="headingOne">
      <h2 className="mb-0">
        <div
          className="btn btn-block text-left "
          type="button"
          data-toggle="collapse"
          data-target={id}
          aria-expanded="true"
          aria-controls="collapseOne"
        >
          <div className="row">
            <div className="col-6">
              <h4>
                {type} <span className="badge ">( {data.length} )</span>
              </h4>
            </div>

            <div className="col-6">
              <h4 className="float-right">Total spent: {total}</h4>
            </div>
          </div>
        </div>
      </h2>
    </div>
  );
};

const ListGroup = (props) => {
  const { data, type, removeItem, editItem } = props;
  const values = data.map((item, index) => {
    return (
      <ListItem
        item={item}
        key={item.id}
        index={item.id}
        removeItem={removeItem}
        editItem={editItem}
      />
    );
  });
  return (
    <div
      id={type}
      className="collapse show"
      aria-labelledby="headingOne"
      data-parent="#accordionExample"
    >
      <ul className="list-group card-body">{values}</ul>
    </div>
  );
};

const Card = (props) => {
  const { items, type, removeItem, editItem } = props;
  const data = GetItems(items, type);

  return (
    <div className="card">
      <CardHeader type={type} data={data} />
      <MaterialTable name={type} />
      <ListGroup
        data={data}
        type={type}
        removeItem={removeItem}
        editItem={editItem}
      />
    </div>
  );
};

export default Card;
