import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { makeStyles } from "@material-ui/core/styles";
import Box from "@material-ui/core/Box";
import Collapse from "@material-ui/core/Collapse";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Typography from "@material-ui/core/Typography";
import Paper from "@material-ui/core/Paper";
import Button from "@material-ui/core/Button";
import { EditButton } from "../ModalButtons";

const useRowStyles = makeStyles({
  root: {
    "& > *": {
      borderBottom: "unset",
    },
  },
});

function Body(props) {
  const { items, updateData } = props;
  const places = items.map((item, i) => {
    return item.place;
  });

  const distinctPlaces = [...new Set(places)];

  const values = distinctPlaces.map((place, index) => {
    const samePlaceItems = items.filter((it, i) => {
      return it.place === place;
    });

    if (samePlaceItems.length < 2) {
      return (
        <SingleRow key={index} row={samePlaceItems[0]} editItem={updateData} />
      );
    } else {
      return (
        <CollapsibleRow
          key={index}
          rows={samePlaceItems}
          editItem={updateData}
        />
      );
    }
  });

  return values;
}

function SingleRow(props) {
  const { row, editItem } = props;
  const classes = useRowStyles();

  return (
    <React.Fragment>
      <TableRow className={classes.root}>
        <TableCell colSpan="3" align="center">
          {row.place}
        </TableCell>
        <TableCell colSpan="3" align="center">
          {row.date}
        </TableCell>
        <TableCell colSpan="3" align="center">
          {row.amount}
        </TableCell>
        <TableCell colSpan="3" align="center">
          <EditButton item={row} editItem={editItem} />
          {/* <Button>Edit</Button>
          <Button>Delete</Button> */}
        </TableCell>
      </TableRow>
    </React.Fragment>
  );
}

function CollapsibleRow(props) {
  const { rows } = props;
  const [open, setOpen] = React.useState(false);
  const classes = useRowStyles();

  return (
    <React.Fragment>
      <TableRow className={classes.root}>
        {/* <TableCell>
          <IconButton
            aria-label="expand row"
            size="small"
            onClick={() => setOpen(!open)}
          >
            {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
          </IconButton>
        </TableCell> */}
        <TableCell colSpan="3" align="center">
          {rows[0].place}
        </TableCell>
        <TableCell colSpan="3" />
        <TableCell colSpan="3" />
        {/* <TableCell align="center">{row.date}</TableCell> */}
        {/* <TableCell align="center">{row.amount}</TableCell> */}
        <TableCell colSpan="3" align="center">
          <Button onClick={() => setOpen(!open)}>Show</Button>
          <Button>Add item</Button>
        </TableCell>

        {/* <TableCell align="center">{row.protein}</TableCell> */}
      </TableRow>
      <TableRow>
        <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={12}>
          <Collapse in={open} timeout="auto" unmountOnExit>
            <Box margin={1}>
              {/* <Typography variant="h6" gutterBottom component="div">
                {rows[0].place}
              </Typography> */}
              <Table size="medium" aria-label="purchases">
                <TableHead>
                  <TableRow>
                    <TableCell colSpan="3" />

                    <TableCell align="center" colSpan="3">
                      Date
                    </TableCell>
                    <TableCell align="center" colSpan="3">
                      Amount
                    </TableCell>
                    <TableCell align="center" colSpan="3">
                      Actions
                    </TableCell>
                    {/* <TableCell align="right">Total price ($)</TableCell> */}
                  </TableRow>
                </TableHead>
                <TableBody>
                  {rows.map((row, i) => (
                    <TableRow key={i}>
                      <TableCell colSpan="3" />
                      <TableCell align="center" colSpan="3">
                        {row.date}
                      </TableCell>
                      <TableCell align="center" colSpan="3">
                        {row.amount}
                      </TableCell>
                      <TableCell align="center" colSpan="3">
                        <Button>Edit</Button>
                        <Button>Delete</Button>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </Box>
          </Collapse>
        </TableCell>
      </TableRow>
    </React.Fragment>
  );
}

export default function MaterialTable(props) {
  const { items, updateData } = props;

  return (
    <TableContainer component={Paper}>
      <Table aria-label="collapsible table">
        <TableHead>
          <TableRow>
            {/* <TableCell /> */}
            <TableCell colSpan="3" align="center">
              {items[0].type}
            </TableCell>
            <TableCell colSpan="3" align="center">
              Date
            </TableCell>
            <TableCell colSpan="3" align="center">
              Amount
            </TableCell>
            <TableCell colSpan="3" align="center">
              Actions
            </TableCell>
            {/* <TableCell align="right">Protein&nbsp;(g)</TableCell> */}
          </TableRow>
        </TableHead>
        <TableBody>
          <Body items={items} updateData={updateData} />
        </TableBody>
      </Table>
    </TableContainer>
  );
}
