const Accordion = (props) => {
  const { items, removeItem, editItem, handleSubmit } = props;
  // const totalSpent = GetTotal(items);
  // console.log("accordion: ");
  return (
    <div className="accordion" id="accordionExample">
      <Total items={items} />

      <Card
        items={items}
        type={"Groceries"}
        removeItem={removeItem}
        editItem={editItem}
        handleSubmit={handleSubmit}
      />
      <Card
        items={items}
        type={"House"}
        removeItem={removeItem}
        editItem={editItem}
        handleSubmit={handleSubmit}
      />
      <Card
        items={items}
        type={"Car"}
        removeItem={removeItem}
        editItem={editItem}
        handleSubmit={handleSubmit}
      />
      <Card
        items={items}
        type={"Other"}
        removeItem={removeItem}
        editItem={editItem}
        handleSubmit={handleSubmit}
      />
    </div>
  );
};

export default Accordion;
