import React from "react";

class Form extends React.Component {
  initialState = {
    type: "",
    place: "",
    amount: 0,
  };

  state = this.initialState;

  handleChange = (event) => {
    const { name, value } = event.target;

    this.setState({
      [name]: value,
    });
  };

  render() {
    const { type, place, amount } = this.state;
    return (
      <form>
        <label htmlFor="type">Type</label>
        <input
          type="text"
          name="type"
          id="type"
          value={type}
          onChange={this.handleChange}
        />
        <label htmlFor="place">Place</label>
        <input
          type="text"
          name="place"
          id="place"
          value={place}
          onChange={this.handleChange}
        />
        <label htmlFor="amount">Amount</label>
        <input
          type="text"
          name="amount"
          id="amount"
          value={amount}
          onChange={this.handleChange}
        />
      </form>
    );
  }
}

export default Form;
