import React, { useState } from "react";
import DatePickerComponent from "../OldVersion/Components/DatePicker";
import { GetTotal } from "../OldVersion/Utilities/Total";

const FilterOptions = (props) => {
  return (
    <div className="container">
      <nav>
        <div class="nav nav-tabs" id="nav-tab" role="tablist">
          <a
            class="nav-link active"
            id="nav-day-tab"
            data-toggle="tab"
            href="#nav-day"
            role="tab"
            aria-controls="nav-day"
            aria-selected="true"
          >
            By Day
          </a>
          <a
            class="nav-link"
            id="nav-period-tab"
            data-toggle="tab"
            href="#nav-period"
            role="tab"
            aria-controls="nav-period"
            aria-selected="false"
          >
            By Period
          </a>
          <a
            class="nav-link"
            id="nav-month-tab"
            data-toggle="tab"
            href="#nav-month"
            role="tab"
            aria-controls="nav-month"
            aria-selected="false"
          >
            By Month
          </a>
        </div>
      </nav>
      <div class="tab-content" id="nav-tabContent">
        <div
          class="tab-pane fade show active"
          id="nav-day"
          role="tabpanel"
          aria-labelledby="nav-day-tab"
        >
          <DatePickerComponent
            label="Pick date"
            // view={null}
            // format="MM/dd/yyyy"
          />
        </div>
        <div
          class="tab-pane fade"
          id="nav-period"
          role="tabpanel"
          aria-labelledby="nav-period-tab"
        >
          <div className="row">
            <div className="col">
              <DatePickerComponent
                label="Pick from date"
                view='["day"], ["month"]'
              />
            </div>
            <div className="col">
              <DatePickerComponent
                label="Pick to date"
                view='["day"], ["month"]'
              />
            </div>
          </div>
        </div>
        <div
          class="tab-pane fade"
          id="nav-month"
          role="tabpanel"
          aria-labelledby="nav-month-tab"
        >
          <DatePickerComponent label="Pick month" view="month" format="MMMM" />
        </div>
      </div>
    </div>
  );
};

function Table(props) {
  const { items } = props;

  const tableRows = items.map((item, i) => {
    return (
      <tr key={i}>
        <td>{item.type}</td>
        <td>{item.place}</td>
        <td>{item.date}</td>
        <td>{item.amount}</td>
      </tr>
    );
  });

  return (
    <table className="table">
      <thead>
        <tr>
          <th scope="col">Type</th>
          <th scope="col">Place</th>
          <th scope="col">Date</th>
          <th scope="col">Amount</th>
        </tr>
      </thead>
      <tbody>{tableRows}</tbody>
    </table>
  );
}

function FilteredItems(props) {
  const { items, filtered, filterByDate } = props;

  if (filtered.length > 0) {
    return (
      <div className="container">
        <div className="row">
          <div className="col-6">
            {/* <DatePickerComponent filterByDate={filterByDate} /> */}
          </div>
          <div className="col-6">Total: {GetTotal(filtered)}</div>
        </div>
        <Table items={filtered} />
      </div>
    );
  } else {
    return (
      <div>
        <div className="row">
          <div className="col-6">
            {/* <DatePickerComponent filterByDate={filterByDate} /> */}
          </div>
          <div className="col-6 alert alert-warning text-center" role="alert">
            <h3 className="align-middle">Nothing to filter</h3>
          </div>
        </div>
        <Table items={items} />
      </div>
    );
  }
}

function FilteredView(props) {
  const { items } = props;

  const [filtered, setFiltered] = useState([]);

  function filterByDate(pickedDate) {
    // console.log("passed date: " + pickedDate);
    const byDate = items.filter((item, i) => {
      return item.date === pickedDate;
    });

    setFiltered(byDate);
  }
  return (
    <div className="container m-3">
      <FilterOptions />
      <FilteredItems
        items={items}
        filtered={filtered}
        filterByDate={filterByDate}
      />
    </div>
  );
}

export default FilteredView;
