import React from "react";
import EditForm from "./EditForm";
import FormToEdit from "./EditForm";

export const Button = (props) => {
  const { name } = props;
  return (
    <button
      type="button"
      className="btn btn-primary btn-lg btn-block m-3"
      data-toggle="modal"
      data-target="#editModal"
    >
      {name}
    </button>
  );
};

const ModalBody = (props) => {
  const { editItem, item } = props;

  return (
    <div className="modal-body">
      <FormToEdit item={item} editItem={editItem} />
    </div>
  );
};

const ModalFooter = () => {
  return (
    <div className="modal-footer">
      <button type="button" className="btn btn-secondary" data-dismiss="modal">
        Cancel
      </button>
    </div>
  );
};

const EditModal = (props) => {
  const { editItem, items } = props;
  return (
    <div className="container">
      {/* <Button name={item.place} /> */}
      <div
        className="modal fade"
        id="editModal"
        data-backdrop="static"
        data-keyboard="false"
        tabIndex="-1"
        aria-labelledby="staticBackdropLabel"
        aria-hidden="true"
      >
        <div className="modal-dialog">
          <div className="modal-content">
            <ModalBody editItem={editItem} />
            <ModalFooter />
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditModal;
