import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import SwipeableViews from "react-swipeable-views";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Typography from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import MaterialTable from "./MaterialTable";

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`full-width-tabpanel-${index}`}
      aria-labelledby={`full-width-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box p={4}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `full-width-tab-${index}`,
    "aria-controls": `full-width-tabpanel-${index}`,
  };
}

const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: theme.palette.background.paper,
    width: 800,
  },
}));

export default function MaterialTabs() {
  const classes = useStyles();
  const theme = useTheme();
  const [value, setValue] = React.useState(0);

  const [items, setItems] = useState([]);

  function getData() {
    fetch("http://localhost:9000/getitems")
      .then((res) => res.json())
      .then((res) => {
        const sortedActivities = res.sort(
          (a, b) => new Date(b.date) - new Date(a.date)
        );
        setItems(sortedActivities);
      });
  }

  useEffect(() => {
    getData();
  }, []);

  const groceries = items.filter((item, i) => {
    return item.type === "Groceries";
  });

  const car = items.filter((item, i) => {
    return item.type === "Car";
  });

  const house = items.filter((item, i) => {
    return item.type === "House";
  });

  const other = items.filter((item, i) => {
    return item.type === "Other";
  });

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  const handleChangeIndex = (index) => {
    setValue(index);
  };

  return (
    <div className={classes.root}>
      <AppBar position="static" color="default">
        <Tabs
          value={value}
          onChange={handleChange}
          indicatorColor="primary"
          textColor="primary"
          variant="fullWidth"
          aria-label="full width tabs example"
        >
          <Tab label="Groceries" {...a11yProps(0)} />
          <Tab label="Car" {...a11yProps(1)} />
          <Tab label="House" {...a11yProps(2)} />
          <Tab label="Other" {...a11yProps(3)} />
        </Tabs>
      </AppBar>
      <SwipeableViews
        axis={theme.direction === "rtl" ? "x-reverse" : "x"}
        index={value}
        onChangeIndex={handleChangeIndex}
      >
        <TabPanel value={value} index={0} dir={theme.direction}>
          <MaterialTable items={groceries} />
        </TabPanel>
        <TabPanel value={value} index={1} dir={theme.direction}>
          <MaterialTable items={car} />
        </TabPanel>
        <TabPanel value={value} index={2} dir={theme.direction}>
          <MaterialTable items={house} />
        </TabPanel>
        <TabPanel value={value} index={3} dir={theme.direction}>
          <MaterialTable items={other} />
        </TabPanel>
      </SwipeableViews>
    </div>
  );
}
