import React from "react";
import Card from "./Card";

function GetTotal(data) {
  const total = data.reduce(
    (totalAmount, item) => totalAmount + parseInt(item.amount, 10),
    0
  );

  return total;
}

const Total = (props) => {
  const { totalSpent } = props;
  let classname;
  if (totalSpent >= 500) {
    classname = "alert alert-danger";
  } else if (totalSpent >= 400) {
    classname = "alert alert-warning";
  } else {
    classname = "alert alert-success";
  }

  return (
    <div>
      <h3 className={classname}>Grand total: {totalSpent}</h3>
    </div>
  );
};

const Accordion = (props) => {
  const { items, removeItem, editItem } = props;
  const totalSpent = GetTotal(items);
  return (
    <div className="accordion" id="accordionExample">
      <Total totalSpent={totalSpent} />
      <Card
        items={items}
        type={"Groceries"}
        removeItem={removeItem}
        editItem={editItem}
      />
      <Card
        items={items}
        type={"House"}
        removeItem={removeItem}
        editItem={editItem}
      />
      <Card
        items={items}
        type={"Car"}
        removeItem={removeItem}
        editItem={editItem}
      />
      <Card
        items={items}
        type={"Other"}
        removeItem={removeItem}
        editItem={editItem}
      />
    </div>
  );
};

export default Accordion;
