import React, { useState } from "react";
import DatePickerComponent from "../OldVersion/Components/DatePicker";

function Table(props) {
  console.log("table body");
  const { items } = props;


  const tableRows = items.map((item, i) => {
    return (
      <tr key={i}>
        <td>{item.type}</td>
        <td>{item.place}</td>
        <td>{item.date}</td>
        <td>{item.amount}</td>
      </tr>
    );
  });

  return (
    <table className="table">
      <thead>
        <tr>
          <th scope="col">Type</th>
          <th scope="col">Place</th>
          <th scope="col">Date</th>
          <th scope="col">Amount</th>
        </tr>
      </thead>
      <tbody>{tableRows}</tbody>
    </table>
  );
};

function FilteredItems(props) {
  const {items, filtered} = props;
  console.log("items: " + items);
  console.log("filtered: " + filtered);

  if (filtered) {
    return <Table items={filtered}/>
  } else {
    return <Table items={items} />
  }

}


function FilteredView(props) {
  const { items } = props;

  const [filtered, setFiltered] = useState([]);


  function filterByDate(pickedDate) {
    console.log("passed date: " + pickedDate);
    const byDate = items.filter((item, i) => {
      return item.date === pickedDate;
    });

    setFiltered(byDate);
  }
  return (
    <div className="container m-3">
      <DatePickerComponent  filterByDate={filterByDate}/>
      <FilteredItems items={items} filtered={filtered}/>
    </div>
  );
};

export default FilteredView;
