import React from "react";
import Form from "./Form";
import EditForm from "./EditForm";

export const Button = (props) => {
  const { name } = props;
  return (
    <button
      type="button"
      className="btn btn-primary btn-lg btn-block m-3"
      data-toggle="modal"
      data-target="#staticBackdrop"
    >
      {name}
    </button>
  );
};

const ModalBody = (props) => {
  const { handleSubmit, item, showEdit } = props;

  if (showEdit === true) {
    return <EditForm item={item} />;
  } else {
    return (
      <div className="modal-body">
        <Form handleSubmit={handleSubmit} item={item} />
      </div>
    );
  }
};

const ModalFooter = () => {
  return (
    <div className="modal-footer">
      <button type="button" className="btn btn-secondary" data-dismiss="modal">
        Close
      </button>
    </div>
  );
};

const ModalHeader = () => {
  return (
    <div className="modal-header">
      <h5 className="modal-title" id="staticBackdropLabel">
        Modal title
      </h5>
      <button
        type="button"
        className="close"
        data-dismiss="modal"
        aria-label="Close"
      >
        <span aria-hidden="true">&times;</span>
      </button>
    </div>
  );
};

const Modal = (props) => {
  const { handleSubmit, name, editItem, item, showEdit } = props;
  return (
    <div className="container">
      <Button name={name} />
      <div
        className="modal fade"
        id="staticBackdrop"
        data-backdrop="static"
        data-keyboard="false"
        tabIndex="-1"
        aria-labelledby="staticBackdropLabel"
        aria-hidden="true"
      >
        <div className="modal-dialog">
          <div className="modal-content">
            <ModalHeader />
            <ModalBody
              handleSubmit={handleSubmit}
              editItem={editItem}
              item={item}
              showEdit={showEdit}
            />
            <ModalFooter />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Modal;
