import React from "react";
import { DateRangePicker, DateRange } from "materialui-daterange-picker";

const RangePicker = (props) => {
  const { filterByPeriod } = props;
  const [open, setOpen] = React.useState(false);
  const [dateRange, setDateRange] = React.useState({});

  const toggle = () => {
    setOpen(!open);
  };

  function Selected(ev) {
    console.log("selected: " + Object.entries(ev));
    const from = new Date(Object.values(ev)[0]).toLocaleDateString();
    const to = new Date(Object.values(ev)[1]).toLocaleDateString();
    let range = {
      from: from,
      to: to,
    };
    filterByPeriod(from, to);
    setDateRange(ev);
  }
  return (
    <div className="container m-3">
      <button className="btn btn-outline-info" onClick={() => toggle()}>
        Open calendar
      </button>
      <DateRangePicker
        open={open}
        toggle={toggle}
        onChange={(range) => Selected(range)}
      />
    </div>
  );
};

export default RangePicker;
