import React, { useState } from "react";
import { Modal, Button } from "react-bootstrap/";
import EditFrom from "./EditForm";

const ListItem = (props) => {
  const { item, index, removeItem } = props;
  function Example(props) {
    const { item } = props;
    const [smShow, setSmShow] = useState(false);

    return (
      <>
        <Button onClick={() => setSmShow(true)}>Edit</Button>{" "}
        <Modal
          show={smShow}
          onHide={() => setSmShow(false)}
          aria-labelledby="staticBackdropLabel"
        >
          {/* <Modal.Header closeButton>
            <Modal.Title id="staticBackdropLabel"></Modal.Title>
          </Modal.Header> */}
          <Modal.Body>
            <EditFrom item={item} />
          </Modal.Body>
          <Modal.Footer>
            <Button
              type="button"
              className="btn btn-secondary"
              data-dismiss="modal"
            >
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }

  return (
    <li className="list-group-item d-flex justify-content-between align-items-center">
      <h5>{item.place}</h5>
      <h5 className="float-right">{item.amount}</h5>
      <Example item={item} />
      <button className="btn" onClick={() => removeItem(index)}>
        Delete
      </button>
    </li>
  );
};

export default ListItem;
