import React, { useState } from "react";
import DatePickerComponent from "../OldVersion/Components/DatePicker";

export function GetTotal(data) {
  const total = data.reduce(
    (totalAmount, item) => totalAmount + parseInt(item.amount, 10),
    0
  );

  return total;
}


function Table(props) {
  console.log("table body");
  const { items } = props;


  const tableRows = items.map((item, i) => {
    return (
      <tr key={i}>
        <td>{item.type}</td>
        <td>{item.place}</td>
        <td>{item.date}</td>
        <td>{item.amount}</td>
      </tr>
    );
  });

  return (
    <table className="table">
      <thead>
        <tr>
          <th scope="col">Type</th>
          <th scope="col">Place</th>
          <th scope="col">Date</th>
          <th scope="col">Amount</th>
        </tr>
      </thead>
      <tbody>{tableRows}</tbody>
    </table>
  );
};

function FilteredItems(props) {
  const {items, filtered, filterByDate} = props;
  console.log("items: " + items);
  console.log("filtered: " + filtered.length);

  if (filtered.length > 0) {
    return (<div className="container">
    <div className="row">
    <div className="col-6"><DatePickerComponent  filterByDate={filterByDate}/></div>
    <div className="col-6">{GetTotal(filtered)}</div>
    </div>
    <Table items={filtered}/></div>)
  } else {
    return (<div>
    <div className="row">
    <div className="col-6"><DatePickerComponent  filterByDate={filterByDate}/></div>
    <div className="col-6 alert alert-warning text-center" role="alert"><h3 className="align-middle">Nothing to filter</h3></div>
    </div>
    <Table items={items}/></div>)
  }

}


function FilteredView(props) {
  const { items } = props;

  const [filtered, setFiltered] = useState([]);


  function filterByDate(pickedDate) {
    // console.log("passed date: " + pickedDate);
    const byDate = items.filter((item, i) => {
      return item.date === pickedDate;
    });

    setFiltered(byDate);
  }
  return (
    <div className="container m-3">

      <FilteredItems items={items} filtered={filtered} filterByDate={filterByDate}/>
    </div>
  );
};

export default FilteredView;
