import React, { useState } from "react";
import { Modal, Button } from "react-bootstrap/";
import EditFrom from "./EditForm";

const ListItem = (props) => {
  const { item, index, removeItem, editItem } = props;
  function Example(props) {
    const { item, editItem } = props;
    const [show, setShow] = useState(false);

    return (
      <>
        <button className="btn float-right" onClick={() => setShow(true)}>
          Edit
        </button>{" "}
        <Modal
          show={show}
          onHide={() => setShow(false)}
          aria-labelledby="staticBackdropLabel"
        >
          <Modal.Header closeButton>
            <Modal.Title>Edit item:</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <EditFrom item={item} editItem={editItem} />
          </Modal.Body>
          <Modal.Footer>
            <Button
              type="button"
              className="btn btn-secondary"
              data-dismiss="modal"
              onClick={() => setShow(false)}
            >
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }

  return (
    <div className="list-group-item d-flex justify-content-between align-items-center row">
      <div className="col-6">
        <h5>{item.place}</h5>
      </div>
      <div className="col-4">
        <h5 className="float-right">{item.amount}</h5>
      </div>
      <div className="col-2">
        <Example item={item} editItem={editItem} />
        <button className="btn float-right" onClick={() => removeItem(index)}>
          Delete
        </button>
      </div>
    </div>
  );
};

export default ListItem;
