import React, { useState } from "react";
import { Modal, Button } from "react-bootstrap/";
import EditFrom from "./EditForm";

const ShowList = (props) => {
  const { items, item } = props;

  const samePlaceItems = items.filter((it, i) => {
    return it.place === item.place;
  });

  if (samePlaceItems.length > 1) {
    return (
      <button
        class="btn btn-outline-info"
        type="button"
        data-toggle="collapse"
        data-target={`#${item.place}`}
        aria-expanded="false"
        aria-controls="collapseExample"
      >
        See List
      </button>
    );
  }
};

const ListItem = (props) => {
  const { item, index, removeItem, editItem, itemsByType } = props;

  function Example(props) {
    const { item, editItem } = props;
    const [show, setShow] = useState(false);

    return (
      <>
        <button
          className="btn btn-outline-warning float-center"
          onClick={() => setShow(true)}
        >
          Edit
        </button>{" "}
        <Modal
          show={show}
          onHide={() => setShow(false)}
          aria-labelledby="staticBackdropLabel"
        >
          <Modal.Header closeButton>
            <Modal.Title>Edit item:</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <EditFrom item={item} editItem={editItem} />
          </Modal.Body>
          <Modal.Footer>
            <Button
              type="button"
              className="btn btn-secondary"
              data-dismiss="modal"
              onClick={() => setShow(false)}
            >
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }

  return (
    <div className="list-group-item d-flex justify-content-between align-items-center row">
      <div className="col-3">
        <h5>{item.place}</h5>
      </div>
      <div className="col-3">
        <ShowList items={itemsByType} item={item} />
      </div>
      <div className="col-4">
        <h5 className="float-right">{item.amount}</h5>
      </div>
      <div className="col-2">
        <Example item={item} editItem={editItem} />
        <button
          className="btn btn-outline-danger float-center"
          type="button"
          onClick={() => removeItem(index)}
        >
          Delete
        </button>
      </div>
      <div class="collapse" id={item.place}>
        <div class="card card-body">
          Anim pariatur cliche reprehenderit, enim eiusmod high life accusamus
          terry richardson ad squid. Nihil anim keffiyeh helvetica, craft beer
          labore wes anderson cred nesciunt sapiente ea proident.
        </div>
      </div>
    </div>
  );
};

export default ListItem;
