import React, { useState } from "react";
import { Modal, Button } from "react-bootstrap/";
// import EditFrom from "./ClassForms/EditForm";
import EditItemForm from "./Forms/EditItemForm";

export const ShowList = (props) => {
  const { items } = props;

  return (
    <div>
      <button
        className="btn btn-outline-info float-left"
        type="button"
        data-toggle="collapse"
        data-target={`#${items[0].place}`}
        aria-expanded="false"
        aria-controls="collapseExample"
      >
        See List
      </button>
    </div>
  );
};

const ListItem = (props) => {
  const { item, index, removeItem, editItem } = props;

  function ModalComponent(props) {
    const { item, editItem } = props;
    const [show, setShow] = useState(false);

    return (
      <>
        <button
          className="btn btn-outline-warning float-center"
          onClick={() => setShow(true)}
        >
          Edit
        </button>{" "}
        <Modal
          show={show}
          onHide={() => setShow(false)}
          aria-labelledby="staticBackdropLabel"
        >
          <Modal.Header closeButton>
            <Modal.Title>Edit item:</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <EditItemForm item={item} editItem={editItem} setShow={setShow} />
          </Modal.Body>
          <Modal.Footer>
            <Button
              type="button"
              className="btn btn-secondary"
              data-dismiss="modal"
              onClick={() => setShow(false)}
            >
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }

  return (
    <div className="list-group-item d-flex justify-content-between align-items-center row">
      <div className="col-3">
        <h5>{item.place}</h5>
      </div>
      <div className="col-2">{item.date}</div>
      <div className="col-2">
        <h5 className="float-right">{item.amount}</h5>
      </div>

      <div className="col-2">
        <ModalComponent item={item} editItem={editItem} />
        <button
          className="btn btn-outline-danger float-center"
          type="button"
          onClick={() => removeItem(index)}
        >
          Delete
        </button>
      </div>
    </div>
  );
};

export default ListItem;
