import React from "react";
import moment from "moment";

function Table(props) {
  const { weeks } = props;

  return (
    <table className="table">
      <TableHead />
      <TableBody weeks={weeks} />
    </table>
  );
}

function TableHead(props) {
  const weekdayNames = [];

  for (let i = 0; i < 7; i++) {
    const weekday = <th key={i}>{moment().weekday([i]).format("dddd")}</th>;
    weekdayNames.push(weekday);
  }

  return (
    <thead>
      <tr>{weekdayNames}</tr>
    </thead>
  );
}

function TableRows(props) {
  const { weeks } = props;
  const rows = weeks.map((week, i) => {
    week = Array.from(week, (item) => (typeof item === "undefined" ? 0 : item));
    const td = week.map((day, i) => {
      //   console.log("day: " + day);
      return <td>{day}</td>;
    });

    return <tr>{td}</tr>;
  });

  //   console.log("rows" + rows);
  return <tbody>{rows}</tbody>;
}

function TableBody(props) {
  const { weeks } = props;
  const rows = weeks.map((week, i) => {
    week = Array.from(week, (item) => (typeof item === "undefined" ? 0 : item));
    const td = week.map((day, i) => {
      //   console.log("day: " + day);
      return <td>{day}</td>;
    });

    return <tr>{td}</tr>;
  });

  //   console.log("rows" + rows);
  return <tbody>{rows}</tbody>;
}

function GetDayName(num) {
  return <span>{moment().date(num).format("dddd")}</span>;
}

function DisplayMoment() {
  var currMonthName = moment().format("MMMM");
  var prevMonthName = moment().subtract(1, "month").format("MMMM");
  let nextMonthName = moment().add(1, "month").format("MMMM");
  let dayCount = moment().daysInMonth();
  let days = [];

  let weeks = [];
  let week = new Array(7);
  for (let index = 1; index <= dayCount; index++) {
    let d = moment().date(index).day();
    if (week[6] !== undefined) {
      weeks.push(week);
      week = new Array(7);
      week[d] = index;
    } else {
      week[d] = index;
    }

    if (index === dayCount) {
      weeks.push(week);
    }
  }

  return (
    <div className="container">
      <div className="row">
        {/* <div className="col-4">{prevMonthName}</div> */}
        <div className="col-4">{currMonthName}</div>
        {/* <div className="col-4">{nextMonthName}</div> */}
      </div>
      <div>
        <TableHead weeks={weeks} />
      </div>
    </div>
  );
}

export default DisplayMoment;
