import React from "react";

function GetTotal(data) {
  const total = data.reduce(
    (totalAmount, item) => totalAmount + parseInt(item.amount, 10),
    0
  );

  return total;
}

const Total = (props) => {
  const { items } = props;

  const totalSpent = GetTotal(items);
  let classname;
  if (totalSpent >= 500) {
    classname = "alert alert-danger";
  } else if (totalSpent >= 400) {
    classname = "alert alert-warning";
  } else {
    classname = "alert alert-success";
  }

  return (
    <div className="group-row">
      <div className="col-4">
        <h3 className={classname}> Incomming: Outgoing: {totalSpent} Left: </h3>
      </div>
      <div className="col-4">
        <h3 className={classname}> Outgoing: {totalSpent} Left: </h3>
      </div>
      <div className="col-4">
        <h3 className={classname}> Left: </h3>
      </div>
    </div>
  );
};

export default Total;
