import React from "react";

class Form extends React.Component {
  initialState = {
    type: "",
    place: "",
    amount: 0,
  };

  state = this.initialState;

  handleChange = (event) => {
    const { name, value } = event.target;

    this.setState({
      [name]: value,
    });
  };

  render() {
    const { type, place, amount } = this.state;
    return (
      <form>
        <div className="form-group">
          <label htmlFor="type">Type</label>
          <input
            className="form-control"
            type="text"
            name="type"
            id="type"
            value={type}
            onChange={this.handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="place">Place</label>
          <input
            className="form-control"
            type="text"
            name="place"
            id="place"
            value={place}
            onChange={this.handleChange}
          />
        </div>

        <label htmlFor="amount">Amount</label>
        <input
          type="text"
          name="amount"
          id="amount"
          value={amount}
          onChange={this.handleChange}
        />
      </form>
    );
  }
}

export default Form;
