import React from "react";
import "./App.css";
import Accordion from "./Accordion";
import Modal from "./Modal";

class App extends React.Component {
  state = {
    items: [],
  };

  getData() {
    fetch("http://localhost:9000/get")
      .then((res) => res.json())
      .then((res) => this.setState({ items: res }));
  }

  componentDidMount() {
    this.getData();
  }

  addData(data) {
    fetch("http://localhost:9000/add", {
      method: "POST", // or 'PUT'
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Success:", data);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  }

  deleteData(id) {
    fetch("http://localhost:9000/remove", {
      method: "POST",
      mode: "no-cors",
      headers: {
        "Content-Type": "application/json",
      },
      body: id,
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Success:", data);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  }

  removeItem = (index) => {
    const { items } = this.state;

    // this.setState({
    //   items: items.filter((item, i) => {
    //     return item.id !== index;
    //   }),
    // });
    const jsonObject = items.filter((item, i) => {
      return item.id === index;
    });

    this.addData(jsonObject);
    this.getData();
  };

  handleSubmit = (item) => {
    item.id = this.state.items.length + 1;

    // this.setState({
    //   items: [...this.state.items, item],
    // });

    this.addData(item);
    this.getData();
  };

  editItem = (item) => {};

  render() {
    const { items } = this.state;
    return (
      <div className="container-fluid">
        <div className="container">
          <p>{this.state.apiResponse}</p>
        </div>
        <Modal handleSubmit={this.handleSubmit} items={items} />
        <Accordion
          items={items}
          handleSubmit={this.handleSubmit}
          removeItem={this.removeItem}
        />
      </div>
    );
  }
}

export default App;
