import React from "react";
import ListItem from "./ListItem";

function GetItems(items, itemtype) {
  return items.filter((value, i) => {
    return value.type === itemtype;
  });
}

const Card = (props) => {
  const { items, type } = props;
  const data = GetItems(items, type);
  let id = "#" + type;
  const values = data.map((item, index) => {
    return <ListItem item={item} />;
  });
  return (
    <div className="card">
      <div className="card-header" id="headingOne">
        <h2 className="mb-0">
          <button
            className="btn btn-link btn-block text-left"
            type="button"
            data-toggle="collapse"
            data-target={id}
            aria-expanded="true"
            aria-controls="collapseOne"
          >
            {type} <span className="text-right">Total spent:</span>
          </button>
        </h2>
        <ul className="list-group card-body">
          <div
            id={type}
            className="collapse show"
            aria-labelledby="headingOne"
            data-parent="#accordionExample"
          >
            <div className="card-body">{values}</div>
          </div>
        </ul>
      </div>
    </div>
  );
};

export default Card;
