import React from "react";
import "../App.css";
import OldAccordion from "./OldAccardion";
import ModalWindow from "./Components/ModalWindow";
import AddItemForm from "./Forms/AddItemForm";

class OldApp extends React.Component {
  state = {
    items: [],
    incomming: 1000,
  };

  getDate(date) {
    return new Date(date).getDate();
  }

  getData() {
    fetch("http://localhost:9000/getitems")
      .then((res) => res.json())
      .then((res) => {
        res.map((item, i) => {
          return console.log("new date: " + this.getDate(item.date));
        });

        //let sortedArray = res.sort((a, b) => (a.date > b.date ? 1 : -1));
        // const withDates = res.map((item, i) => {
        //   console.log("date " + new Date(item.date));
        //   return (item.date = new Date(item.date));
        // });
        // const sortedActivities = res.sort((a, b) => b.date - a.date);

        this.setState({ items: res });
      });
  }

  componentDidMount() {
    this.getData();
  }

  addData(data) {
    fetch("http://localhost:9000/additem", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((data) => this.setState({ items: data }))
      .catch((error) => {
        console.error("Error:", error);
      });
  }

  deleteData(data) {
    fetch("http://localhost:9000/removeitem", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((data) => this.setState({ items: data }))
      .catch((error) => {
        console.error("Error:", error);
      });
  }

  updateData(data) {
    fetch("http://localhost:9000/updateitem", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((data) => this.setState({ items: data }))
      .catch((error) => {
        console.error("Error:", error);
      });
  }

  removeItem = (index) => {
    const { items } = this.state;

    const jsonObject = items.filter((item, i) => {
      return item.id === index;
    });

    this.deleteData(jsonObject[0]);
  };

  handleSubmit = (item) => {
    let idArray = this.state.items.map((item) => {
      return item.id;
    });

    var maxId = Math.max(...idArray);
    item.id = maxId + 1;

    this.addData(item);
  };

  editItem = (item) => {
    this.updateData(item);
  };

  render() {
    const { items, incomming } = this.state;
    return (
      <div className="container-fluid m-3">
        <ModalWindow
          buttonName="Add new expense item"
          headerName="Add item info"
          classDescription="btn btn-primary btn-lg btn-block"
          bodyComponent={<AddItemForm handleSubmit={this.handleSubmit} />}
        />
        <OldAccordion
          items={items}
          incomming={incomming}
          handleSubmit={this.handleSubmit}
          removeItem={this.removeItem}
          editItem={this.editItem}
        />
      </div>
    );
  }
}

export default OldApp;
