import React from "react";
import ListItem, { ShowList } from "./ListItem";

function GetItemsByType(items, itemtype) {
  return items.filter((value, i) => {
    return value.type === itemtype;
  });
}

function GetTotal(data) {
  const total = data.reduce(
    (totalAmount, item) => totalAmount + parseInt(item.amount, 10),
    0
  );

  return total;
}

const CardHeader = (props) => {
  const { type, itemsByType } = props;
  // const id = `#{type}`;
  const total = GetTotal(itemsByType);
  return (
    <div className="card-header" id="headingOne">
      <h2 className="mb-0">
        <div
          className="btn btn-block text-left "
          type="button"
          data-toggle="collapse"
          data-target={`#${type}`}
          aria-expanded="true"
          aria-controls="collapseOne"
        >
          <div className="row">
            <div className="col-6">
              <h4>
                {type} <span className="badge ">( {itemsByType.length} )</span>
              </h4>
            </div>

            <div className="col-6">
              <h4 className="float-right">Total: {total}</h4>
            </div>
          </div>
        </div>
      </h2>
    </div>
  );
};

const ListGroup = (props) => {
  const { itemsByType, type, removeItem, editItem } = props;

  const places = itemsByType.filter((item, i) => {
    return item.place;
  });
  const distinctPlaces = places.filter(
    (value, index, categoryArray) => categoryArray.indexOf(value) === index
  );

  const values = distinctPlaces.map((place, index) => {
    const samePlaceItems = itemsByType.filter((it, i) => {
      return it.place === place;
    });
    console.log("type: ");
    console.log("same place from " + place + " " + samePlaceItems.length);
    if (samePlaceItems.length < 2) {
      return (
        <ListItem
          itemsByType={itemsByType}
          item={samePlaceItems}
          key={samePlaceItems.id}
          index={samePlaceItems.id}
          removeItem={removeItem}
          editItem={editItem}
        />
      );
    } else {
      return (
        <div className="container-fluid">
          <div className="row">
            <div className="col-4">{samePlaceItems[0].place}</div>
            <div className="col-4">
              <ShowList items={samePlaceItems} />
            </div>
            <div className="col-4"></div>
          </div>
        </div>
      );
    }
  });
  return (
    <div
      id={type}
      className="collapse show"
      aria-labelledby="headingOne"
      data-parent="#accordionExample"
    >
      <div className="list-group card-body">{values}</div>
    </div>
  );
};

const OldCard = (props) => {
  const { items, type, removeItem, editItem } = props;
  const itemsByType = GetItemsByType(items, type);

  //console.log("card name: " + type);
  return (
    <div className="card">
      <CardHeader type={type} itemsByType={itemsByType} />
      <ListGroup
        itemsByType={itemsByType}
        type={type}
        removeItem={removeItem}
        editItem={editItem}
      />
    </div>
  );
};

export default OldCard;
