import React, { useState } from "react";
import DatePickerComponent from "../OldVersion/Components/DatePicker";

const TableBody = (props) => {
  console.log("table body");
  const { items } = props;
  const tableRows = items.map((item, i) => {
    return (
      <tr>
        <td>{item.type}</td>
        <td>{item.place}</td>
        <td>{item.date}</td>
        <td>{item.amount}</td>
      </tr>
    );
  });

  return <tbody>{tableRows}</tbody>;
};

const FilteredView = (props) => {
  const { items } = props;
  console.log("reloaded");
  const [filtered, setFiltered] = useState([]);

  function filterByDate(pickedDate) {
    console.log("passed date: " + pickedDate);
    const byDate = items.filter((item, i) => {
      return item.date === pickedDate;
    });

    setFiltered(byDate);
    return <TableBody items={filtered} />;
  }

  //   const tableRows = filteredItems.map((item, i) => {
  //     console.log("items: " + item);
  //     return (
  //       <tr>
  //         <td>{item.type}</td>
  //         <td>{item.place}</td>
  //         <td>{item.date}</td>
  //         <td>{item.amount}</td>
  //       </tr>
  //     );
  //   });

  return (
    <div className="container m-3">
      <DatePickerComponent filterByDate={filterByDate} />
      <table className="table">
        <thead>
          <tr>
            <th scope="col">Type</th>
            <th scope="col">Place</th>
            <th scope="col">Date</th>
            <th scope="col">Amount</th>
          </tr>
        </thead>
        <TableBody items={items} />
        {/* <tbody>{tableRows}</tbody> */}
      </table>
    </div>
  );
};

export default FilteredView;
