import React, { useState } from "react";
import { Modal, Button } from "react-bootstrap/";

export const ModalButton = (props) => {
  const { setShow, name } = props;
  return (
    <button
      className="btn btn-outline-warning float-center"
      onClick={() => setShow(true)}
    >
      {name}
    </button>
  );
};

export const ModalHeader = (props) => {
  const { name } = props;
  return (
    <Modal.Header closeButton>
      <Modal.Title>{name}</Modal.Title>
    </Modal.Header>
  );
};

export const ModalBody = (props) => {
  const { bodyComponent } = props;

  return (
    <Modal.Body>
      {bodyComponent}
      {/* <EditItemForm item={item} editItem={editItem} setShow={setShow} /> */}
    </Modal.Body>
  );
};

export const ModalFooter = (props) => {
  const { setShow } = props;

  return (
    <Modal.Footer>
      <Button
        type="button"
        className="btn btn-secondary"
        onClick={() => setShow(false)}
      >
        Close
      </Button>
    </Modal.Footer>
  );
};

const ModalWindow = (props) => {
  const { item, editItem, headerName, buttonName, bodyComponent } = props;
  const [show, setShow] = useState(false);
  return (
    <>
      <ModalButton setShow={setShow} name={buttonName} />
      <Modal
        show={show}
        onHide={() => setShow(false)}
        aria-labelledby="staticBackdropLabel"
      >
        <ModalHeader name={headerName} />
        <ModalBody bodyComponent={bodyComponent} />
        <ModalFooter setShow={setShow} />
      </Modal>
    </>
  );
};

export default ModalWindow;
