import React, { useState } from "react";
import { Modal } from "react-bootstrap/";
import Button from "@material-ui/core/Button";
import { ThemeProvider } from "@material-ui/core/styles";
import { AddButton } from "../StyleComponents/StyledButtons";
// import * as UIButton from "@material-ui/core/Button";

const ModalButton = (props) => {
  const {
    setShow,
    name,
    classDescription,
    size,
    variant,
    color,
    startIcon,
    theme,
  } = props;

  if (name === "add") {
    return <AddButton>{name}</AddButton>;
  } else {
    return (
      <ThemeProvider theme={theme}>
        <Button
          startIcon={startIcon}
          size={size}
          variant={variant}
          color={color}
          className={classDescription}
          onClick={() => setShow(true)}
        >
          {name}
        </Button>
      </ThemeProvider>
    );
  }
};

const ModalHeader = (props) => {
  const { name } = props;
  return (
    <Modal.Header closeButton>
      <Modal.Title>{name}</Modal.Title>
    </Modal.Header>
  );
};

const ModalBody = (props) => {
  const { bodyComponent, setShow } = props;

  return (
    <Modal.Body>
      {React.cloneElement(bodyComponent, { setShow: setShow })}
    </Modal.Body>
  );
};

const ModalFooter = (props) => {
  const { setShow } = props;

  return (
    <Modal.Footer>
      <Button
        type="button"
        className="btn btn-secondary"
        onClick={() => setShow(false)}
      >
        Close
      </Button>
    </Modal.Footer>
  );
};

const ModalWindow = (props) => {
  const {
    headerName,
    buttonName,
    bodyComponent,
    classDescription,
    size,
    color,
    variant,
    startIcon,
    theme,
    name,
  } = props;
  const [show, setShow] = useState(false);
  return (
    <>
      <ModalButton
        name={name}
        startIcon={startIcon}
        setShow={setShow}
        name={buttonName}
        size={size}
        variant={variant}
        color={color}
        theme={theme}
        classDescription={classDescription}
      />
      <Modal
        show={show}
        onHide={() => setShow(false)}
        aria-labelledby="staticBackdropLabel"
      >
        <ModalHeader name={headerName} />
        <ModalBody bodyComponent={bodyComponent} setShow={setShow} />
        <ModalFooter setShow={setShow} />
      </Modal>
    </>
  );
};

export default ModalWindow;
