import React, { useState } from "react";
import { Modal, Button } from "react-bootstrap/";
import EditForm from "./EditForm";

const Example = (props) => {
  const { item, editItem, handleSubmit } = props;
  const [show, setShow] = useState(false);

  return (
    <>
      <button
        className="btn btn-outline-warning float-center"
        onClick={() => setShow(true)}
      >
        Edit
      </button>{" "}
      <Modal
        show={show}
        onHide={() => setShow(false)}
        aria-labelledby="staticBackdropLabel"
      >
        <Modal.Header closeButton>
          <Modal.Title>Add item:</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <EditForm
            item={item}
            editItem={editItem}
            handleSubmit={handleSubmit}
            show={show}
          />
        </Modal.Body>
        <Modal.Footer>
          <Button
            type="button"
            className="btn btn-secondary"
            data-dismiss="modal"
            onClick={() => setShow(false)}
          >
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

const SamePlaceItem = (props) => {
  const { item, index, removeItem, editItem, handleSubmit } = props;

  return (
    <div className="list-group-item d-flex justify-content-between align-items-center row">
      <div className="col-5">
        <h6 className="float-right">{item.date}</h6>
      </div>
      <div className="col-5">
        <h6 className="float-right">{item.amount}</h6>
      </div>

      <div className="col-2">
        <Example item={item} editItem={editItem} handleSubmit={handleSubmit} />
        <button
          className="btn btn-outline-danger float-center"
          type="button"
          onClick={() => removeItem(index)}
        >
          Delete
        </button>
      </div>
    </div>
  );
};

export default SamePlaceItem;
