import React, { useState } from "react";
import { Modal, Button } from "react-bootstrap/";
import EditFrom from "./EditForm";

const SamePlaceItem = (props) => {
  const { item, index, removeItem, editItem, itemsByType } = props;

  function Example(props) {
    const { item, editItem } = props;
    const [show, setShow] = useState(false);

    return (
      <>
        <button
          className="btn btn-outline-warning float-center"
          onClick={() => setShow(true)}
        >
          Edit
        </button>{" "}
        <Modal
          show={show}
          onHide={() => setShow(false)}
          aria-labelledby="staticBackdropLabel"
        >
          <Modal.Header closeButton>
            <Modal.Title>Add item:</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <EditFrom item={item} editItem={editItem} />
          </Modal.Body>
          <Modal.Footer>
            <Button
              type="button"
              className="btn btn-secondary"
              data-dismiss="modal"
              onClick={() => setShow(false)}
            >
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      </>
    );
  }

  return (
    <div className="list-group-item d-flex justify-content-between align-items-center row">
      <div className="col-3">{item.date}</div>
      <div className="col-3">
        <h5 className="float-right">{item.amount}</h5>
      </div>

      <div className="col-2">
        <Example item={item} editItem={editItem} />
        <button
          className="btn btn-outline-danger float-center"
          type="button"
          onClick={() => removeItem(index)}
        >
          Delete
        </button>
      </div>
    </div>
  );
};

export default SamePlaceItem;
