import React, { useState } from "react";
import DatePickerComponent from "../OldVersion/Components/DatePicker";
import { GetTotal } from "../OldVersion/Utilities/Total";

const FilterOptions = (props) => {
  return (
    <div className="container">
      <nav>
        <div class="nav nav-tabs" id="nav-tab" role="tablist">
          <a
            class="nav-link active"
            id="nav-home-tab"
            data-toggle="tab"
            href="#nav-home"
            role="tab"
            aria-controls="nav-home"
            aria-selected="true"
          >
            By Day
          </a>
          <a
            class="nav-link"
            id="nav-profile-tab"
            data-toggle="tab"
            href="#nav-profile"
            role="tab"
            aria-controls="nav-profile"
            aria-selected="false"
          >
            By Period
          </a>
          <a
            class="nav-link"
            id="nav-contact-tab"
            data-toggle="tab"
            href="#nav-contact"
            role="tab"
            aria-controls="nav-contact"
            aria-selected="false"
          >
            By Month
          </a>
        </div>
      </nav>
      <div class="tab-content" id="nav-tabContent">
        <div
          class="tab-pane fade show active"
          id="nav-home"
          role="tabpanel"
          aria-labelledby="nav-home-tab"
        >
          ...
        </div>
        <div
          class="tab-pane fade"
          id="nav-profile"
          role="tabpanel"
          aria-labelledby="nav-profile-tab"
        >
          ...
        </div>
        <div
          class="tab-pane fade"
          id="nav-contact"
          role="tabpanel"
          aria-labelledby="nav-contact-tab"
        >
          ...
        </div>
      </div>
    </div>
  );
};

function Table(props) {
  const { items } = props;

  const tableRows = items.map((item, i) => {
    return (
      <tr key={i}>
        <td>{item.type}</td>
        <td>{item.place}</td>
        <td>{item.date}</td>
        <td>{item.amount}</td>
      </tr>
    );
  });

  return (
    <table className="table">
      <thead>
        <tr>
          <th scope="col">Type</th>
          <th scope="col">Place</th>
          <th scope="col">Date</th>
          <th scope="col">Amount</th>
        </tr>
      </thead>
      <tbody>{tableRows}</tbody>
    </table>
  );
}

function FilteredItems(props) {
  const { items, filtered, filterByDate } = props;

  if (filtered.length > 0) {
    return (
      <div className="container">
        <div className="row">
          <div className="col-6">
            <DatePickerComponent filterByDate={filterByDate} />
          </div>
          <div className="col-6">Total: {GetTotal(filtered)}</div>
        </div>
        <Table items={filtered} />
      </div>
    );
  } else {
    return (
      <div>
        <div className="row">
          <div className="col-6">
            <DatePickerComponent filterByDate={filterByDate} />
          </div>
          <div className="col-6 alert alert-warning text-center" role="alert">
            <h3 className="align-middle">Nothing to filter</h3>
          </div>
        </div>
        <Table items={items} />
      </div>
    );
  }
}

function FilteredView(props) {
  const { items } = props;

  const [filtered, setFiltered] = useState([]);

  function filterByDate(pickedDate) {
    // console.log("passed date: " + pickedDate);
    const byDate = items.filter((item, i) => {
      return item.date === pickedDate;
    });

    setFiltered(byDate);
  }
  return (
    <div className="container m-3">
      <FilterOptions />
      <FilteredItems
        items={items}
        filtered={filtered}
        filterByDate={filterByDate}
      />
    </div>
  );
}

export default FilteredView;
