import React from "react";
import Groceries from "./Groceries";
import CarItems from "./CarItems";
import HouseItems from "./HouseItems";

const TableHeader = () => {
  return (
    <thead>
      <tr>
        <th>Name</th>
        <th>Total</th>
      </tr>
    </thead>
  );
};

function GetGroceries(values) {
  return values.filter((value, i) => {
    return value.type === "food";
  });
}

function GetCarItems(values) {
  return values.filter((value, i) => {
    return value.type === "car";
  });
}

function GetHosueItems(values) {
  return values.filter((value, i) => {
    return value.type === "house";
  });
}

const TableBody = (props) => {
  const groceries = GetGroceries(props.itemData);
  const carItems = GetCarItems(props.itemData);
  const rows = props.itemData.map((row, index) => {
    return (
      //<Groceries groceries={groceries} />
      <tr key={index}>
        {/* <td>{index}</td> */}
        <td>{row.place}</td>
        <td>{row.amount}</td>
        <td>
          <button onClick={() => props.removeItem(index)}>Delete</button>
        </td>
      </tr>
    );
  });

  return (
    <tbody>
      <Groceries groceries={groceries} />
      <CarItems carItems={carItems} />
    </tbody>
  );
};

const Table = (props) => {
  const { itemData, removeItem } = props;

  return (
    <table>
      <TableHeader />
      <TableBody itemData={itemData} removeItem={removeItem} />
    </table>
  );
};

export default Table;
