import React from "react";
import "./App.css";
import Table from "./Table";

function GetJson() {
  return [
    {
      type: "food",
      amount: 25,
    },
    {
      type: "food",
      amount: 45,
    },
    {
      type: "car",
      amount: 5,
    },
    {
      type: "house",
      amount: 125,
    },
  ];
}

class App extends React.Component {
  state = {
    items: GetJson(),
  };
  removeItem = (index) => {
    const { items } = this.state;

    this.setState({
      items: items.filter((item, i) => {
        return i !== index;
      }),
    });
  };
  render() {
    const { items } = this.state;
    return (
      <div className="container">
        <Table itemData={items} removeItem={this.removeItem} />
      </div>
    );
  }
}

export default App;
