import React from "react";

export function GetTotal(data) {
  const total = data.reduce(
    (totalAmount, item) => totalAmount + parseInt(item.amount, 10),
    0
  );

  return total;
}

const Total = (props) => {
  const { items, incomming } = props;

  const totalSpent = GetTotal(items);
  const left = incomming - totalSpent;

  let classname;
  if (left < 0) {
    classname = "alert alert-danger";
  } else if (left <= 200) {
    classname = "alert alert-warning";
  } else {
    classname = "alert alert-success";
  }

  return (
    <div className="row">
      <div className="col-4">
        <h5 className="alert alert-success"> Incomming: {incomming}</h5>
      </div>
      <div className="col-4">
        <h5 className="alert alert-danger"> Outgoing: {totalSpent}</h5>
      </div>
      <div className="col-4">
        <h5 className={classname}> Left: {left}</h5>
      </div>
    </div>
  );
};

export default Total;
