import React from "react";
import MaterialTable from "material-table";

export default function MaterialTableDemo(props) {
  const { name, items } = props;

  const [values, setValues] = React.useState([]); //table data

  React.useEffect(() => {
    return () => {
      setValues(items);
    };
  }, []);
  const [state, setState] = React.useState({
    columns: [
      { title: "ID", field: "id" },
      { title: "Type", field: "type" },
      { title: "Place", field: "place" },
      { title: "Amount", field: "amount", type: "numeric" },
    ],
    data: values,
  });

  items.map((item, i) => {
    return state.data.push(item);
  });
  return (
    <MaterialTable
      options={{
        search: false,
      }}
      title={name}
      columns={state.columns}
      data={data}
      editable={{
        onRowAdd: (newData) =>
          new Promise((resolve) => {
            setTimeout(() => {
              resolve();
              setState((prevState) => {
                const data = [...prevState.data];
                data.push(newData);
                return { ...prevState, data };
              });
            }, 600);
          }),
        onRowUpdate: (newData, oldData) =>
          new Promise((resolve) => {
            setTimeout(() => {
              resolve();
              if (oldData) {
                setState((prevState) => {
                  const data = [...prevState.data];
                  data[data.indexOf(oldData)] = newData;
                  return { ...prevState, data };
                });
              }
            }, 600);
          }),
        onRowDelete: (oldData) =>
          new Promise((resolve) => {
            setTimeout(() => {
              resolve();
              setState((prevState) => {
                const data = [...prevState.data];
                data.splice(data.indexOf(oldData), 1);
                return { ...prevState, data };
              });
            }, 600);
          }),
      }}
    />
  );
}
