import React from "react";
import MaterialTable from "material-table";

export default function MaterialTableDemo(props) {
  const { type, handleSubmit, removeItem, editItem } = props;

  const [data, setData] = React.useState([]); //table data

  React.useEffect(() => {
    fetch("http://localhost:9000/getitems")
      .then((res) => res.json())
      .then((res) => {
        // let t = res.filter((item) => {
        //   return item.type === type;
        // });
        setData(res);
      })
      .catch((error) => {});
  }, []);
  const columns = [
    { title: "Type", field: "type" },
    { title: "Place", field: "place" },
    { title: "Amount", field: "amount", type: "numeric" },
  ];

  //const tableName = type + " " + data.length;

  const tableName = (pro) => {
    const tname = pro + " " + data.length;
    return (
      <div>
        <span className="warning">{tname}</span>
      </div>
    );
  };

  const handleRowAdd = (newData, resolve) => {
    // fetch("http://localhost:9000/additem", {
    //   method: "POST",
    //   headers: {
    //     "Content-Type": "application/json",
    //   },
    //   body: JSON.stringify(newData),
    // })
    //   .then((response) => response.json())
    //   .then((res) => {
    //     let dataToAdd = [...data];
    //     dataToAdd.push(newData);
    //     setData(dataToAdd);
    //     resolve();
    //   })
    //   .catch((error) => {
    //     console.error("Error:", error);
    //     resolve();
    //   });
    newData.type = type;
    handleSubmit(newData);
    let dataToAdd = [...data];
    dataToAdd.push(newData);
    setData(dataToAdd);
    resolve();
  };

  const handleRowUpdate = (newData, oldData, resolve) => {
    console.log("row updated: " + newData);
    editItem(newData);
    const dataUpdate = [...data];
    const index = oldData.tableData.id;
    dataUpdate[index] = newData;
    setData([...dataUpdate]);
    resolve();
  };

  const handleRowDelete = (oldData, resolve) => {
    console.log("row deleted: " + oldData);
    removeItem(oldData.id);
    const dataDelete = [...data];
    const index = oldData.tableData.id;
    dataDelete.splice(index, 1);
    setData([...dataDelete]);
    resolve();
  };

  return (
    <MaterialTable
      options={{
        search: false,
        grouping: true,
      }}
      title={tableName(type)}
      columns={columns}
      data={data}
      editable={{
        onRowUpdate: (newData, oldData) =>
          new Promise((resolve) => {
            handleRowUpdate(newData, oldData, resolve);
          }),
        onRowAdd: (newData) =>
          new Promise((resolve) => {
            handleRowAdd(newData, resolve);
          }),
        onRowDelete: (oldData) =>
          new Promise((resolve) => {
            handleRowDelete(oldData, resolve);
          }),
      }}
    />
    // <MaterialTable
    //   options={{
    //     search: false,
    //   }}
    //   title={name}
    //   columns={state.columns}
    //   data={data}
    //   editable={{
    //     onRowAdd: (newData) =>
    //       new Promise((resolve) => {
    //         setTimeout(() => {
    //           resolve();
    //           setData((prevData) => {
    //             const data = [...prevData];
    //             data.push(newData);
    //             return { ...prevData, data };
    //           });
    //         }, 600);
    //       }),
    //     onRowUpdate: (newData, oldData) =>
    //       new Promise((resolve) => {
    //         setTimeout(() => {
    //           resolve();
    //           if (oldData) {
    //             setData((prevData) => {
    //               const data = [...prevData];
    //               data[data.indexOf(oldData)] = newData;
    //               return { ...prevData, data };
    //             });
    //           }
    //         }, 600);
    //       }),
    //     onRowDelete: (oldData) =>
    //       new Promise((resolve) => {
    //         setTimeout(() => {
    //           resolve();
    //           setData((prevData) => {
    //             const data = [...prevData];
    //             data.splice(data.indexOf(oldData), 1);
    //             return { ...prevData, data };
    //           });
    //         }, 600);
    //       }),
    //   }}
    // />
  );
}
