import React, { useEffect, useState } from "react";
import moment from "moment";

function Table(props) {
  const { weeks } = props;

  return (
    <table className="table">
      <TableHead />
      <TableBody weeks={weeks} />
    </table>
  );
}

function TableHead() {
  const weekdayNames = [];

  for (let i = 0; i < 7; i++) {
    const weekday = (
      <th key={i} className="align-middle">
        {moment().weekday([i]).format("ddd")}
      </th>
    );
    weekdayNames.push(weekday);
  }

  return (
    <thead>
      <tr>{weekdayNames}</tr>
    </thead>
  );
}

function NextMonthButton(props) {
  const { currentMonth } = props;

  let nextMonth = currentMonth.add(1, "month");
  let weeks = GetWeeksOfMonth(nextMonth);

  return weeks;
}

// function TableRows(props) {
//   const { weeks } = props;
//   const rows = weeks.map((week, i) => {
//     week = Array.from(week, (item) => (typeof item === "undefined" ? 0 : item));
//     const td = week.map((day, i) => {
//       //   console.log("day: " + day);
//       return <td>{day}</td>;
//     });

//     return <tr>{td}</tr>;
//   });

//   //   console.log("rows" + rows);
//   return <tbody>{rows}</tbody>;
// }

function TableBody(props) {
  const { weeks } = props;
  const rows = weeks.map((week, i) => {
    week = Array.from(week, (item) => (typeof item === "undefined" ? 0 : item));
    const td = week.map((day, i) => {
      //   console.log("day: " + day);
      return <td>{day}</td>;
    });

    return <tr>{td}</tr>;
  });

  //   console.log("rows" + rows);
  return <tbody>{rows}</tbody>;
}

function GetDayName(num) {
  return <span>{moment().date(num).format("dddd")}</span>;
}

function GetWeeksOfMonth(month) {
  let dayCount = month.daysInMonth();
  let weeks = [];
  let week = new Array(7);
  for (let index = 1; index <= dayCount; index++) {
    let d = month.date(index).day();
    if (week[6] !== undefined) {
      weeks.push(week);
      week = new Array(7);
      week[d] = index;
    } else {
      week[d] = index;
    }

    if (index === dayCount) {
      weeks.push(week);
    }
  }

  return weeks;
}

function DisplayMoment() {
  const [selectedMonth, setMonth] = useState(moment());
  const [weeks, setWeeks] = useState(GetWeeksOfMonth(selectedMonth));

  function loadNextMonth(props) {
    let nextMonth = props.add(1, "month");

    setMonth(nextMonth);
    setWeeks(GetWeeksOfMonth(selectedMonth));
  }

  function loadPreviousMonth(props) {
    let previousMonth = props.subtract(1, "month");

    setMonth(previousMonth);
    setWeeks(GetWeeksOfMonth(selectedMonth));
  }

  return (
    <div className="container border">
      <div className="row">
        <div className="col-3 text-center">
          <button
            className="btn btn-outline-info"
            onClick={() => loadPreviousMonth(selectedMonth)}
          >
            Previos
          </button>
        </div>
        <div className="col-6 text-center">{selectedMonth.format("MMMM")}</div>
        <div className="col-3 text-center">
          <button
            className="btn btn-outline-info"
            onClick={() => loadNextMonth(selectedMonth)}
          >
            Next
          </button>
        </div>
      </div>
      <div>
        <Table weeks={weeks} selectedMonth={selectedMonth} />
      </div>
    </div>
  );
}

export default DisplayMoment;
