import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { makeStyles } from "@material-ui/core/styles";
import Box from "@material-ui/core/Box";
import Collapse from "@material-ui/core/Collapse";
import IconButton from "@material-ui/core/IconButton";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Typography from "@material-ui/core/Typography";
import Paper from "@material-ui/core/Paper";
import KeyboardArrowDownIcon from "@material-ui/icons/KeyboardArrowDown";
import KeyboardArrowUpIcon from "@material-ui/icons/KeyboardArrowUp";
import Button from "@material-ui/core/Button";

const useRowStyles = makeStyles({
  root: {
    "& > *": {
      borderBottom: "unset",
    },
  },
});

// function createData(name, calories, fat, carbs, protein, price) {
//   return {
//     name,
//     calories,
//     fat,
//     carbs,
//     protein,
//     price,
//     history: [
//       { date: "2020-01-05", customerId: "11091700", amount: 3 },
//       { date: "2020-01-02", customerId: "Anonymous", amount: 1 },
//     ],
//   };
// }

function Body(props) {
  const { items } = props;
  const places = items.map((item, i) => {
    return item.place;
  });

  const distinctPlaces = [...new Set(places)];

  const values = distinctPlaces.map((place, index) => {
    const samePlaceItems = items.filter((it, i) => {
      return it.place === place;
    });

    if (samePlaceItems.length < 2) {
      return <SingleRow key={index} row={samePlaceItems[0]} />;
    } else {
      return <CollapsibleRow key={index} rows={samePlaceItems} />;
    }
  });

  return values;
}

function SingleRow(props) {
  const { row } = props;
  // const [open, setOpen] = React.useState(false);
  const classes = useRowStyles();

  return (
    <React.Fragment>
      <TableRow className={classes.root}>
        <TableCell colSpan="3" align="center">
          {row.place}
        </TableCell>
        <TableCell colSpan="3" align="center">
          {row.date}
        </TableCell>
        <TableCell colSpan="3" align="center">
          {row.amount}
        </TableCell>
        <TableCell colSpan="3" align="center">
          <Button>Edit</Button>
          <Button>Delete</Button>
        </TableCell>
      </TableRow>
    </React.Fragment>
  );
}

function CollapsibleRow(props) {
  const { rows } = props;
  const [open, setOpen] = React.useState(false);
  const classes = useRowStyles();

  return (
    <React.Fragment>
      <TableRow className={classes.root}>
        {/* <TableCell>
          <IconButton
            aria-label="expand row"
            size="small"
            onClick={() => setOpen(!open)}
          >
            {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
          </IconButton>
        </TableCell> */}
        <TableCell component="th" scope="row">
          {rows[0].place}
        </TableCell>
        {/* <TableCell align="center">{row.date}</TableCell> */}
        {/* <TableCell align="center">{row.amount}</TableCell> */}
        <TableCell align="center">
          <Button onClick={() => setOpen(!open)}>Show</Button>
          <Button>Add item</Button>
        </TableCell>

        {/* <TableCell align="center">{row.protein}</TableCell> */}
      </TableRow>
      <TableRow>
        <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
          <Collapse in={open} timeout="auto" unmountOnExit>
            <Box margin={1}>
              <Typography variant="h6" gutterBottom component="div">
                {rows[0].place}
              </Typography>
              <Table size="medium" aria-label="purchases">
                <TableHead>
                  <TableRow>
                    <TableCell />
                    <TableCell />

                    <TableCell align="right">Date</TableCell>
                    <TableCell align="right">Amount</TableCell>
                    <TableCell align="center">Actions</TableCell>
                    {/* <TableCell align="right">Total price ($)</TableCell> */}
                  </TableRow>
                </TableHead>
                <TableBody>
                  {rows.map((row, i) => (
                    <TableRow key={i}>
                      <TableCell />
                      <TableCell />
                      <TableCell component="th" scope="row">
                        {row.date}
                      </TableCell>
                      <TableCell>{row.amount}</TableCell>
                      <TableCell>
                        <Button>Edit</Button>
                        <Button>Delete</Button>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </Box>
          </Collapse>
        </TableCell>
      </TableRow>
    </React.Fragment>
  );
}

export default function MaterialTable() {
  const [items, setItems] = useState([]);

  function getData() {
    fetch("http://localhost:9000/getitems")
      .then((res) => res.json())
      .then((res) => {
        const sortedActivities = res.sort(
          (a, b) => new Date(b.date) - new Date(a.date)
        );
        setItems(sortedActivities);
      });
  }

  useEffect(() => {
    getData();
  }, []);

  const groceries = items.filter((item, i) => {
    return item.type === "Groceries";
  });

  return (
    <TableContainer component={Paper}>
      <Table aria-label="collapsible table">
        <TableHead>
          <TableRow>
            {/* <TableCell /> */}
            <TableCell colSpan="3" align="center">
              Groceries
            </TableCell>
            <TableCell colSpan="3" align="center">
              Date
            </TableCell>
            <TableCell colSpan="3" align="center">
              Amount
            </TableCell>
            <TableCell colSpan="3" align="center">
              Actions
            </TableCell>
            {/* <TableCell align="right">Protein&nbsp;(g)</TableCell> */}
          </TableRow>
        </TableHead>
        <TableBody>
          <Body items={groceries} />
          {/* {groceries.map((row, i) => (
            <CollapsibleRow key={i} row={row} />
          ))} */}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
