import React from "react";
import "./App.css";
import Table from "./Table";
import Card from "./Card";

function GetJson() {
  return [
    {
      id: 1,
      type: "food",
      place: "maxima",
      amount: 25,
    },
    {
      id: 2,
      type: "food",
      place: "maxima",
      amount: 35,
    },
    {
      id: 3,
      type: "food",
      place: "rimi",
      amount: 45,
    },
    {
      id: 4,
      type: "car",
      place: "wess",
      amount: 5,
    },
    {
      id: 5,
      type: "house",
      place: "Elektrum",
      amount: 125,
    },
    {
      id: 6,
      type: "house",
      place: "TET",
      amount: 125,
    },
  ];
}

class App extends React.Component {
  state = {
    items: GetJson(),
  };
  removeItem = (index) => {
    const { items } = this.state;

    this.setState({
      items: items.filter((item, i) => {
        return i !== index;
      }),
    });
  };
  render() {
    const { items } = this.state;
    return (
      <div className="container">
        <Table itemData={items} removeItem={this.removeItem} />
        <div className="accordion">
          <Card />
        </div>
      </div>
    );
  }
}

export default App;
