import React from "react";
import Card from "./Card";

function GetTotal(data) {
  const total = data.reduce(
    (totalAmount, item) => totalAmount + parseInt(item.amount, 10),
    0
  );

  return total;
}

const Total = (props) => {
  const { totalSpent } = props;

  return (
    <div>
      <h3>
        Grand total: {totalSpent}{" "}
        <span>{totalSpent > 500 ? "NOT OK" : "OK"}</span> .
      </h3>
    </div>
  );
};

const Accordion = (props) => {
  const { items, removeItem } = props;
  const totalSpent = GetTotal(items);
  return (
    <div className="accordion" id="accordionExample">
      <Total totalSpent={totalSpent} />
      <Card items={items} type={"Groceries"} removeItem={removeItem} />
      <Card items={items} type={"House"} removeItem={removeItem} />
      <Card items={items} type={"Car"} removeItem={removeItem} />
      <Card items={items} type={"Other"} removeItem={removeItem} />
    </div>
  );
};

export default Accordion;
