import React from "react";
import EditModal from "./EditModal";

const ListItem = (props) => {
  const { item, index, removeItem, editItem } = props;
  return (
    <li className="list-group-item d-flex justify-content-between align-items-center">
      <h5>{item.place}</h5>

      <h5 className="float-right">{item.amount}</h5>
      {/* <EditModal name="Edit" item={item} editItem={editItem} /> */}
      <button
        type="button"
        className="btn btn-primary btn-lg btn-block m-3"
        data-toggle="modal"
        data-target="#editModal"
      >
        {item.name}
      </button>
      <button className="btn" onClick={() => removeItem(index)}>
        Delete
      </button>
    </li>
  );
};

export default ListItem;
