import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";
import SwipeableViews from "react-swipeable-views";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Typography from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import MaterialTable from "./MaterialTable";
import { GetTotal } from "../Utilities/Total";
import Paper from "@material-ui/core/Paper";

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`full-width-tabpanel-${index}`}
      aria-labelledby={`full-width-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box p={3}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `full-width-tab-${index}`,
    "aria-controls": `full-width-tabpanel-${index}`,
  };
}

const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: theme.palette.background.paper,
    width: 900,
  },
}));

export default function MaterialTabs() {
  const classes = useStyles();
  const theme = useTheme();
  const [value, setValue] = React.useState(0);

  const [items, setItems] = useState([]);

  function getData() {
    fetch("http://localhost:9000/getitems")
      .then((res) => res.json())
      .then((res) => {
        const sortedActivities = res.sort(
          (a, b) => new Date(b.date) - new Date(a.date)
        );
        setItems(sortedActivities);
      });
  }
  useEffect(() => {
    getData();
  }, []);

  function updateData(data) {
    fetch("http://localhost:9000/updateitem", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((data) => {
        const sortedActivities = data.sort(
          (a, b) => new Date(b.date) - new Date(a.date)
        );
        console.log("response: " + sortedActivities);
        setItems(sortedActivities);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  }
  console.log("items: " + items);
  const groceries = items.filter((item, i) => {
    return item.type === "Groceries";
  });

  const car = items.filter((item, i) => {
    return item.type === "Car";
  });

  const house = items.filter((item, i) => {
    return item.type === "House";
  });

  const other = items.filter((item, i) => {
    return item.type === "Other";
  });

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  const handleChangeIndex = (index) => {
    setValue(index);
  };

  return (
    <div className={classes.root}>
      <AppBar position="static" color="default">
        <Tabs
          value={value}
          onChange={handleChange}
          indicatorColor="primary"
          textColor="primary"
          variant="fullWidth"
          aria-label="full width tabs example"
        >
          <Tab label="Groceries" {...a11yProps(0)} />
          <Tab label="Car" {...a11yProps(1)} />
          <Tab label="House" {...a11yProps(2)} />
          <Tab label="Other" {...a11yProps(3)} />
        </Tabs>
      </AppBar>
      <SwipeableViews
        axis={theme.direction === "rtl" ? "x-reverse" : "x"}
        index={value}
        onChangeIndex={handleChangeIndex}
      >
        <TabPanel value={value} index={0} dir={theme.direction}>
          <MaterialTable items={groceries} updateData={updateData} />
          <Paper className="border m-3">
            <h3 className="text-center p-3">Total: {GetTotal(groceries)}</h3>
          </Paper>
        </TabPanel>
        <TabPanel value={value} index={1} dir={theme.direction}>
          <MaterialTable items={car} updateData={updateData} />
          <Paper>
            <h4 className="text-center">Total: {GetTotal(car)}</h4>
          </Paper>
        </TabPanel>
        <TabPanel value={value} index={2} dir={theme.direction}>
          <MaterialTable items={house} updateData={updateData} />
          <Paper>
            <h4 className="text-center">Total: {GetTotal(house)}</h4>
          </Paper>
        </TabPanel>
        <TabPanel value={value} index={3} dir={theme.direction}>
          <MaterialTable items={other} updateData={updateData} />
          <Paper>
            <h4 className="text-center">Total: {GetTotal(other)}</h4>
          </Paper>
        </TabPanel>
      </SwipeableViews>
    </div>
  );
}
